mapper.insert:
INSERT INTO sys_spring_scheduled_task (
    id, task_name, cron_expression, task_class, task_method, task_parameter,
    status, description, create_by, create_time, update_by, update_time, del_flag
) VALUES (
    #{id}, #{taskName}, #{cronExpression}, #{taskClass}, #{taskMethod}, #{taskParameter},
    #{status}, #{description}, #{createBy}, #{createTime}, #{updateBy}, #{updateTime}, #{delFlag}
)

mapper.update:
UPDATE sys_spring_scheduled_task
SET del_flag = 1
WHERE id = #{id}
